// cypress/e2e/home.cy.js

describe('Home Page', () => {
 beforeEach(() => {
 cy.visit('/');
 });

 it('deve exibir o logo do Next.js', () => {
 cy.get('[data-testid=

]')
 .should('be.visible')
 .and('have.attr', 'alt', 'Next.js logo');
 });

 it('deve exibir a lista de instruções com dois itens', () => {
 cy.get('ol li').should('have.length', 2);
 cy.get('ol li').first().should('contain.text', 'Get started by editing');
 cy.get('ol li').first().find('code').should('contain.text', 'src/app/page.tsx');
 cy.get('ol li').eq(1).should('contain.text', 'Save and see your changes instantly');
 });

 it('deve conter um botão de deploy que abre em nova aba', () => {
 cy.get('[data-testid=

] img')
 .should('have.attr', 'alt', 'Vercel logomark');
 });

 it('deve conter um link para a documentação', () => {
 cy.get('[data-testid=

]')
 .should('be.visible')
 .and('contain.text', 'Read our docs')
 .and('have.attr', 'href', 'https://nextjs.org/docs?utm_source=create-next-app&utm_medium=appdir-template-tw&utm_campaign=create-next-app')
 .and('have.attr', 'target', '_blank');
 });

 it('deve conter links no rodapé', () => {
 // Verifica o link Learn
 cy.get('[data-testid=

]')
 .should('be.visible')
 .and('contain.text', 'Examples')
 .and('have.attr', 'href', 'https://vercel.com/templates?framework=next.js&utm_source=create-next-app&utm_medium=appdir-template-tw&utm_campaign=create-next-app');

 // Verifica o link para nextjs.org
 cy.get('[data-testid=

]')
 .should('be.visible')
 .and('contain.text', 'Go to nextjs.org')
 .and('have.attr', 'href', 'https://nextjs.org?utm_source=create-next-app&utm_medium=appdir-template-tw&utm_campaign=create-next-app');
 });

 it('deve ter layout responsivo', () => {
 // Testa em viewport mobile
 cy.viewport('iphone-6');
 cy.get('main').should('have.class', 'items-center');
 cy.get('ol').should('have.class', 'text-center');
 cy.get('[data-testid=

]').parent().should('have.class', 'flex-col');

 // Testa em viewport desktop
 cy.viewport(1024, 768);
 cy.get('main').should('have.class', 'items-center');
 cy.get('ol').should('have.class', 'text-center');
 cy.get('[data-testid=

]').parent().should('have.class', 'flex-col');

 // Verificação de classes SM em viewport maior
 cy.viewport('macbook-15');
 // Aguarda um momento para a mudança de layout
 cy.wait(500);
 cy.get('main').should('have.class', 'items-center');
 cy.get('[data-testid=

]').parent().should('have.class', 'sm:flex-row');
 });

 // Teste de acessibilidade básico
 it('deve ter imagens com atributos alt adequados', () => {
 cy.get('img').each(($img) => {
 cy.wrap($img).should('have.attr', 'alt');
 });
 });
});

